mapper.select:
SELECT IFNULL(COUNT(*) , 0)
FROM information
WHERE point_id = #{pointId}
AND type = #{type}

mapper.insert:
insert into information (id, point_id, user_id, 
  type, content, remark_count, 
  click_count, create_at, isLock
  )
values (#{id,jdbcType=INTEGER}, #{pointId,jdbcType=INTEGER}, #{userId,jdbcType=INTEGER}, 
  #{type,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR}, #{remarkCount,jdbcType=INTEGER}, 
  #{clickCount,jdbcType=INTEGER}, #{createAt,jdbcType=TIMESTAMP}, #{islock,jdbcType=INTEGER}
  )
